import React, { Component } from 'react';
import { Table } from 'reactstrap';
import axios from 'axios';
import Buttons from './ButtonComponent';

class TableComponent extends Component {
    // constructor(props) {
    //     super(props);
    //     this.state = {
    //         data: []
    //     };
    //     this.getData = this.getData.bind(this);
    //   }

    componentWillMount() {
        this.getData();
    }

    async getData () {
        const data = axios.get ("http://localhost:8000/api/deliveries.php")
            .then(
                res => this.setState({data: Object.keys(res.data).map(function(i){return res.data[i]})}),    
            )
            .catch(error => {
                console.log(error);
            });
        console.log (data);
    }

    render () {
        return (
            <div>
                <h1>Deliveries</h1>
                <Table className="table">
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Date</th>
                            <th>Name</th>
                            <th>Driver</th>
                            <th>&nbsp;</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <th scope="row">123</th>
                            <td>123</td>
                            <td>123</td>
                            <td>{this.state.driver_id}</td>
                            <Buttons getData = {this.getData}/>
                        </tr>
                    </tbody>
                </Table>
            </div>
        );
    }
}

export default TableComponent;
