import React, { Component } from 'react';
import { Table } from 'reactstrap';
import axios from 'axios';
import Buttons from './ButtonComponent';

class TableComponent extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id: [],
            deliveriesItems: []
        };
        this.getData = this.getData.bind(this);
      }

    componentWillMount() {
        this.getData();
    }

    async getData () {
        const data = await axios.get ("http://localhost:8000/api/deliveries.php")
            .catch(error => {
                console.log(error);
            });

        this.setState({
            id: Object.keys(data.data),
            deliveriesItems: Object.values(data.data)
        });

        console.log (data);
        console.log (data.data);
    }

    render () {
        let deliveriesIDs = this.state.id;
        let deliveriesItems = this.state.deliveriesItems;
        console.log('1: ' + this.state.id.length);
        console.log('2: ' + this.state.deliveriesItems);
        return (
            <div>
                <h1>Deliveries</h1>
                <Table className="table">
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Date</th>
                            <th>Name</th>
                            <th>Driver</th>
                            <th>&nbsp;</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            {/* {deliveriesIDs.map((id, i) => <th scope="row" key = {i}>{id}</th>)} */}
                            {/* {deliveriesItems.map((item, i) => <td key = {i}>{item.date}</td>)} */}
                            {/* {deliveriesItems.map((item, i) => <td key = {i}>{item.name}</td>)} */}
                            {/* {deliveriesItems.map((item, i) => <td key = {i}>{item.driver_id}</td>)} */}
                            <Buttons getData = {this.getData}/>
                        </tr>
                    </tbody>
                </Table>
            </div>
        );
    }
}

export default TableComponent;
