import React, { Component } from 'react';
import { Button } from 'reactstrap';
import axios from 'axios';

class ButtonComponent extends Component {
    constructor(props){
        super(props);
        this.removeRecord = this.removeRecord.bind(this);
    }

    removeRecord () {
        const data = axios.delete ("http://localhost:8000/api/deliveries.php?id=as")
                        .catch(error => {
                            console.log(error);
                        });
        console.log(data);
    }

    render () {
        return (
            <React.Fragment>
                <Button outline color="primary" onClick = {this.props.getData}>Edit</Button>{' '}
                <Button outline color="danger" onClick = {this.removeRecord}>Delete</Button>{' '}
            </React.Fragment>
        );
    }
}

export default ButtonComponent;
