import React, { Component } from 'react';
import { Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';
import axios from 'axios';

export default class FormComponent extends Component {
    constructor(props){
        super(props);
        this.googleInput = React.createRef();
        this.createDelivery = this.createDelivery.bind(this);
    }

    createDelivery (e) {
        e.preventDefault();
        var d= e.target.value;
        console.log(d);
        // axios.post ("http://localhost:8000/api/deliveries.php", {
        //                 "date": this.refs.dateValue.value,
        //                 "name": this.refs.nameValue.value,
        //                 "driver_id": this.refs.driverValue.value
        // })
        // .then(res=>{
        //     console.log(res);
        // })
    }

    render() {
        return (
            <Form onSubmit={this.createDelivery}>
                <FormGroup>
                    <Label for="exampleDate">Date</Label>
                    <Input  type="date" name="date" id="exampleDate" placeholder="date placeholder" />
                </FormGroup>
                <FormGroup>
                    <Label for="Name">Name</Label>
                    <Input ref={ this.googleInput } type="text" name="name" id="Name" placeholder="Your name here" />
                </FormGroup>
                <FormGroup>
                    <Label for="exampleSelect">Select Driver</Label>
                    <Input ref="driverValue" type="select" name="select" id="exampleSelect">
                        <option>1</option>
                        <option>2</option>
                        <option>3</option>
                    </Input>
                </FormGroup>
                <Button type="submit" value="Submit">Submit</Button>
            </Form>
        );
    }
}
