import React, { Component } from 'react';
import './css/main.css';
import axios from 'axios';
import Header from './components/HeaderComponent';
import Table from './components/TableComponent';
import Form from './components/FormComponent';

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: {},
            drivers: {},
            inputDisabled: false,
            newDeliveryButtonClicked: false,
            showComponent: true
        }
        this.getData = this.getData.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.newDeliveryButtonClicked = this.newDeliveryButtonClicked.bind(this);
    }

    componentDidMount() {
        this.getData();
    }

    async getData() {
        const drivers = await axios.get("http://localhost:8000/api/drivers.php");
        const data = await axios.get("http://localhost:8000/api/deliveries.php")
            .catch(error => {
                console.log(error);
            });

        this.setState({
            data: data.data,
            drivers: drivers.data,
        });
    }

    handleSubmit(e) {
        e.preventDefault();
        axios.post("http://localhost:8000/api/deliveries.php?id=15", {
            "date": e.target.elements.date.value,
            "name": e.target.elements.deliveryName.value,
            "driver_id": e.target.elements.selectDriver.value
        })
        .then(res => {
            console.log(res);
        })
    }

    newDeliveryButtonClicked () {
        this.setState ({
            newDeliveryButtonClicked: true
        })
    }

    render() {
        return (
            <div className="App">
                <Header onClick={this.newDeliveryButtonClicked}/>
                <hr />
                {
                this.state.showComponent &&
                <Form 
                    value={this.state.drivers} 
                    onSubmit={this.handleSubmit} 
                    inputDisabled={this.state.inputDisabled}
                />
                }
                <hr />
                <hr />
                <Table value={this.state} />

            </div>
        );
    }
}

export default App;
