import React, { Component } from 'react';
import { Button } from 'reactstrap';
import axios from 'axios';

class ButtonComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            data: []
        };
        this.getData = this.getData.bind(this);
        this.removeRecord = this.removeRecord.bind(this);
      }

    getData () {
        axios.get ("http://localhost:8000/api/deliveries.php")
        .then(
            res => this.setState({data: Object.keys(res.data).map(function(i){return res.data[i]})}),
            console.log(this.state.data)
        )
        .catch(error => {
            console.log(error);
        });
    }

    removeRecord () {
        axios.delete ("http://localhost:8000/api/deliveries.php?id=123")
        .then(
            res => this.setState({data: Object.keys(res.data).map(function(i){return res.data[i]})}),
            console.log(this.state.data)
        )
        .catch(error => {
            console.log(error);
        })
    }

    render () {
        return (
            <div>
                <Button outline color="primary" onClick = {this.getData}>Edit</Button>{' '}
                <Button outline color="danger" onClick = {this.removeRecord}>Delete</Button>{' '}
            </div>
        );
    }
}

export default ButtonComponent;
