import React, { Component } from 'react';
import { Table, Button } from 'reactstrap';
import axios from 'axios';

class TableComponent extends Component {
    constructor(props) {
        super(props);
        this.state = {
            'data': []
        };
        this.getData = this.getData.bind(this);
      }

    componentWillMount() {
        this.getData();
    }

    getData () {
        axios.get ("http://localhost:8000/api/deliveries.php")
        .then(
            res => this.setState({'data': res.data}),
            console.log (this.state.data)
        )
        .catch(error => {
            console.log(error);
        });
    }

    render () {
        return (
            <div>
                <h1>Deliveries</h1>
                <Table className="table">
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Date</th>
                            <th>Name</th>
                            <th>Driver</th>
                            <th>&nbsp;</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <th scope="row">{this.state.data.map(function(index){
                                return console.log(index);
                            })}</th>
                            <td>{this.state.date}</td>
                            <td>{this.state.name}</td>
                            <td>{this.state.driver_id}</td>
                            <Button outline color="primary" onClick = {this.getData}>Edit</Button>{' '}
                            <Button outline color="danger">Delete</Button>{' '}
                        </tr>
                    </tbody>
                </Table>
            </div>
        );
    }
}

export default TableComponent;
