import React, { Component } from 'react';
import { Table, Button } from 'reactstrap';
import axios from 'axios';

class TableComponent extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: []
        };
        this.getData = this.getData.bind(this);
      }

    componentWillMount() {
        this.getData();
    }

    getData () {
        axios.get ("http://localhost:8000/api/deliveries.php")
        .then(
            res => this.setState({data: Object.keys(res.data).map(function(i){return res.data[i]})}),

        )
        .catch(error => {
            console.log(error);
        });
    }

    render () {
        console.log (this.state.data)
        return (
            <div>
                <h1>Deliveries</h1>
                <Table className="table">
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Date</th>
                            <th>Name</th>
                            <th>Driver</th>
                            <th>&nbsp;</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.state.data.map(function(index, obj){
                            return (
                                <tr>
                                <th scope="row">{index}</th>
                                <td>{obj.date}</td>
                                <td>{obj.name}</td>
                                <td>{obj.driver_id}</td>
                                </tr>
                            );
                        },

                        <Button outline color="primary" onClick = {this.getData}>Edit</Button>,
                        <Button outline color="danger">Delete</Button>
                        )}  
                    </tbody>
                </Table>
            </div>
        );
    }
}

export default TableComponent;
