import React, { Component } from 'react';
import { Table } from 'reactstrap';
import axios from 'axios';
import Buttons from './ButtonComponent';

class TableComponent extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: {},
            drivers: {}
        };
        this.getData = this.getData.bind(this);
    }

    componentWillMount() {
        this.getData();
    }

    async getData() {
        const drivers = await axios.get("http://localhost:8000/api/drivers.php");
        const data = await axios.get("http://localhost:8000/api/deliveries.php")
            .catch(error => {
                console.log(error);
            });

        this.setState({
            data: data.data,
            drivers: drivers.data
        });

        console.log(data);
        console.log(data.data);
    }

    render() {
        let deliveries = this.state.data;
        // console.log(JSON.stringify(deliveries));
        console.log('asdiauhsdahsdu');
        return (
            <div>
                <h1>Deliveries</h1>
                <Table className="table">
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Date</th>
                            <th>Name</th>
                            <th>Driver</th>
                            <th>&nbsp;</th>
                        </tr>
                    </thead>
                    <tbody>

                        {Object.keys(deliveries).map((item, i) => (
                            <tr key={i}>
                                <th scope="row">{item}</th>                            
                                <td key = {i}>{deliveries[item].date}</td>
                                <td key = {i}>{deliveries[item].name}</td>
                                <td key = {i}>{this.state.drivers[deliveries[item]["driver_id"]]}</td> 
                                <Buttons key={i+'hello'} value={item} />
                            </tr>
                        ))}
                    </tbody>
                </Table>
            </div>
        );
    }
}

export default TableComponent;
